mapper.update:
UPDATE seckill
goods_id= #{goodsId}, goods_name=#{goodsName}, seckill_stock=#{seckillStock}, seckill_starttime=#{seckillStartTime}, seckill_endtime=#{seckillEndTime}, seckill_creationtime=#{seckillCreationTime} seckill_price=#{seckillPrice}
,version = version + 1 WHERE id = #{id}

mapper.select:
SELECT seckill_id, user_phone, seckilled_state, seckilled_creationtime, seckilled_price, seckilled_number, seckilled.goods_id as goods_id, seckill.goods_name as goods_name, seckill_stock, seckill_starttime, seckill_endtime, seckill_creationtime, goods_title, goods_img, goods_detail, goods_price, goods_stock FROM seckilled LEFT JOIN seckill ON(seckill.id = seckilled.seckill_id) LEFT JOIN goods ON(goods.id = seckilled.goods_id) WHERE seckill_id IN
#{id}
AND user_phone = #{userPhone}
